// Extract the AI agent's final response
const agentOutput = $input.item.json.output || $input.item.json.text || '';
const messageData = $('Normalize Message Data').item.json;

return {
  json: {
    response_text: agentOutput,
    customer_id: messageData.customer_id,
    channel: messageData.channel,
    sender_id_raw: messageData.sender_id_raw
  }
};
