// Extract message data from Instagram webhook payload OR test request
const webhookData = $input.all()[0].json;
const startTime = Date.now();

console.log('Full webhook data:', JSON.stringify(webhookData, null, 2));

// Check if this is a test request (has test_mode flag)
const isTestMode = webhookData.test_mode === true || webhookData.body?.test_mode === true;

// n8n wraps it in 'body'
let data = webhookData.body || webhookData;

console.log('Extracted data:', JSON.stringify(data, null, 2));
console.log('Test mode:', isTestMode);

// Modern Instagram API format (entry -> changes)
if (data.entry && data.entry[0] && data.entry[0].changes) {
  const change = data.entry[0].changes[0];

  console.log('Change object:', JSON.stringify(change, null, 2));

  if (change.field === 'messages' && change.value) {
    const messageData = change.value;

    // Validate required fields
    if (!messageData.sender || !messageData.message || !messageData.message.text) {
      console.log('Missing required fields');
      return [{ json: { error: 'Missing required fields', skip: true } }];
    }

    const senderId = messageData.sender.id;
    const recipientId = messageData.recipient?.id;
    const timestamp = messageData.timestamp;
    const messageText = messageData.message.text;
    const messageId = messageData.message.mid;

    // Build customer ID
    const customerId = `instagram:${senderId}`;

    console.log('Successfully extracted:', { customerId, messageText, isTestMode });

    return [{
      json: {
        customer_id: customerId,
        sender_id: senderId,
        recipient_id: recipientId,
        message_text: messageText,
        message_id: messageId,
        timestamp: timestamp,
        start_time: startTime,
        channel: 'instagram',
        is_test_mode: isTestMode,  // ← NEW FLAG
        skip: false
      }
    }];
  }
}

// Old Messenger format (entry -> messaging)
if (data.entry && data.entry[0] && data.entry[0].messaging) {
  const messaging = data.entry[0].messaging[0];

  if (!messaging || !messaging.message) {
    return [{ json: { error: 'Not a message event', skip: true } }];
  }

  const senderId = messaging.sender.id;
  const recipientId = messaging.recipient.id;
  const timestamp = messaging.timestamp;
  const messageText = messaging.message.text || '';
  const messageId = messaging.message.mid;

  if (!messageText.trim()) {
    return [{ json: { error: 'No text in message', skip: true } }];
  }

  const customerId = `instagram:${senderId}`;

  return [{
    json: {
      customer_id: customerId,
      sender_id: senderId,
      recipient_id: recipientId,
      message_text: messageText,
      message_id: messageId,
      timestamp: timestamp,
      start_time: startTime,
      channel: 'instagram',
      skip: false
    }
  }];
}

// Invalid format
console.log('Invalid webhook format - no matching structure found');
return [{ json: { error: 'Invalid webhook payload', skip: true } }];

// Parse OpenAI response
const aiResponse = $input.first().json;
// Use optional chaining just in case these inputs are missing in test runs
const messageData = $('Extract Message Data').first()?.json || {};
const contextData = $('Prepare Enhanced Context').first()?.json || {};

let responseData = {
    text: '',
    action: 'answer',
    order_data: null
};

try {
  // 1. Extract content safely
  let rawContent;

  // Check the path from your specific input example
  if (aiResponse.output && aiResponse.output[0] && aiResponse.output[0].content && aiResponse.output[0].content[0] && aiResponse.output[0].content[0].text) {
    rawContent = aiResponse.output[0].content[0].text;
  } 
  // Check standard OpenAI paths
  else if (aiResponse.message && aiResponse.message.content) {
    rawContent = aiResponse.message.content;
  } 
  else if (aiResponse.choices && aiResponse.choices[0] && aiResponse.choices[0].message && aiResponse.choices[0].message.content) {
    rawContent = aiResponse.choices[0].message.content;
  } 
  else {
    rawContent = '';
  }

  // 2. Normalize rawContent to a JSON Object
  let parsedObj;

  if (typeof rawContent === 'object' && rawContent !== null) {
    // It's already an object (like in your input example)
    parsedObj = rawContent;
  } else {
    // It's a string, ensure it is treated as such
    let contentStr = String(rawContent).trim();

    // Clean Markdown
    if (contentStr.startsWith('```json')) {
      contentStr = contentStr.replace(/```json\s*/g, '').replace(/```\s*$/g, '');
    } else if (contentStr.startsWith('```')) {
      contentStr = contentStr.replace(/```\s*/g, '');
    }

    contentStr = contentStr.trim();

    if (contentStr) {
      try {
        parsedObj = JSON.parse(contentStr);
      } catch (parseError) {
         // If JSON parse fails, treat the whole string as the text response
         parsedObj = { text: contentStr, action: 'answer' };
      }
    } else {
      throw new Error('Extracted content is empty');
    }
  }

  // 3. Map to Internal Schema
  // We handle "response", "text", or "message" keys to be safe
  responseData.text = parsedObj.text || parsedObj.response || parsedObj.message || '';
  responseData.action = parsedObj.action || 'answer';
  responseData.order_data = parsedObj.order_data || null;

  // 4. Validate
  if (!responseData.text) {
    throw new Error('Missing text field in response');
  }

  // Logic correction for create_order
  if (responseData.action === 'create_order' && !responseData.order_data) {
    responseData.action = 'request_info';
    responseData.order_data = null;
  }

} catch (e) {
  // Pure JS error handling
  const errorMessage = e.message || String(e);
  console.log('Failed to parse AI response: ' + errorMessage);

  // Fallback extraction for the specific failure case in your input
  let fallbackText = 'عذراً، حدث خطأ. من فضلك حاول مرة أخرى.';

  // Safe traversal in pure JS without TS assertions
  if (aiResponse && 
      aiResponse.output && 
      aiResponse.output[0] && 
      aiResponse.output[0].content && 
      aiResponse.output[0].content[0] && 
      aiResponse.output[0].content[0].text && 
      aiResponse.output[0].content[0].text.response) {

      fallbackText = aiResponse.output[0].content[0].text.response;
  }

  responseData = {
    text: fallbackText,
    action: 'answer',
    order_data: null
  };
}

return [{
  json: {
    response_text: responseData.text,
    action: responseData.action,
    order_data: responseData.order_data,
    intent: (contextData.intent_analysis && contextData.intent_analysis.intent) ? contextData.intent_analysis.intent : 'unknown',
    tokens_used: (aiResponse.usage && aiResponse.usage.total_tokens) ? aiResponse.usage.total_tokens : 0,
    sender_id: messageData.sender_id,
    customer_id: messageData.customer_id,
    user_message: messageData.message_text,
    start_time: messageData.start_time,
    channel: messageData.channel
  }
}];

// Process order result and update response text
const orderResult = $input.first().json;
const prevData = $('Should Create Order?').first().json;

let finalText = prevData.response_text;

if (orderResult.success) {
  // Replace placeholder with actual order ID
  const shortOrderId = orderResult.order_id.substring(0, 8).toUpperCase();
  finalText = finalText.replace('{{ORDER_ID}}', shortOrderId);
  finalText = finalText.replace('{{ERROR_MESSAGE}}', '');
} else {
  // Replace with error message
  finalText = finalText.replace('{{ORDER_ID}}', '');
  finalText = finalText.replace('{{ERROR_MESSAGE}}', orderResult.message || orderResult.error);

  // If text still has placeholders, use a fallback message
  if (finalText.includes('{{')) {
    const errorMsg = orderResult.message || 'حدث خطأ في إنشاء الطلب';
    finalText = `عذراً، ${errorMsg}. من فضلك حاول مرة أخرى أو تواصل معنا.`;
  }
}

return [{
  json: {
    ...prevData,
    response_text: finalText,
    order_result: orderResult,
    order_id: orderResult.order_id || null,
    order_success: orderResult.success
  }
}];

// Log error and prepare fallback response
const errorData = $input.first().json;
console.error('Workflow Error:', JSON.stringify(errorData, null, 2));

return [{
  json: {
    error: true,
    message: 'عذراً، حدث خطأ في معالجة رسالتك. سنتواصل معك قريباً.',
    timestamp: new Date().toISOString(),
    original_error: errorData.message || 'Unknown'
  }
}];

